mapper.select:
select cid, logic_code, name, phone, photo, type, level,score, login_name, login_password,
email, address, create_time, modify_time, create_user_code, modify_user_code, is_delete
from system_user
where login_name = #{searchContent.userName,jdbcType=VARCHAR}
and login_password = #{searchContent.password,jdbcType=VARCHAR}
